mapper.update:
update users
set usernm=#{userNm}
, alias=#{alias}
, addr1=#{addr1}
, addr2=#{addr2}
, zipcode=#{zipcode}
, pass=#{pass}
, reg_dt=sysdate
, filename=nvl(#{filename, jdbcType=VARCHAR} ,filename)
, realFilename=nvl(#{realFilename,jdbcType=VARCHAR},realfilename)
where   userid=#{userId}
